import React from 'react'
import Place from "./Place";
class Login extends React.Component{
    constructor() {
        super();
        this.state = {
            place: null,
            service: null,
            worker: null,
            isLoadedPlace: 0,
            isLoadedService:0,
            isLoadedeWorker: 0
        }
    }
    render() {
        let style = {

        }
        let getParamsFromString = ()=>{
            let search = window.location.search.replace("?", "");
            search = search.split("&");
            let ret = {}
            let fParam = search[0];
            let sParam = search[1];
            let tParam = search[2];
            ret[fParam.split("=")[0]]=(+fParam.split("=")[1]);
            ret[sParam.split("=")[0]]=(+sParam.split("=")[1]);
            ret[tParam.split("=")[0]]=(+tParam.split("=")[1]);
            return ret;
        }
        console.log(getParamsFromString())
        return (
            <div>
                <div style={style}>
                    <div>Введите регистрационные данные: </div>
                    <div>
                        Имя<br/>
                        <input type="text" name="name" />
                        <br/>
                        Номер телефона
                        <br/>
                        <input type="tel" name="phone"/>
                    </div>
                    <div>
                        <br/><br/>
                        <div>
                            Данные о заказе
                        </div>
                        {(()=>{
                            if (this.state.isLoadedeWorker*this.state.isLoadedPlace*this.state.isLoadedService)
                            {
                                console.log(this.state)
                                return <div>
                                    <div>Тип услуги: {""+this.state.service.type}</div>
                                    <div>Название услуги: </div>
                                    <div>Описание услуги: </div>
                                    <div>Продолжительность услуги: </div>
                                    <div>ФИО исполнителя: </div>
                                    <div>Номер телефона исполнителя: </div>
                                    <div>Адрес пункта проведения: </div>
                                </div>
                            }
                            else {
                                let condition = this.props.location.search;
                                condition = condition.replace("?", "");

                                if (this.state.place === null)
                                    fetch("http://localhost:3001/customRequest/Select%20*%20from%20places" +
                                        " where id="+getParamsFromString().placeID).then(
                                        (response)=>{
                                            response.json().then(
                                                (json)=>{
                                                    let newState = {};
                                                    newState.place = json;
                                                    newState.isLoadedPlace = 1;
                                                    this.setState(newState);
                                                },
                                                reason => {

                                                }
                                            )
                                        },
                                        (reject)=>{
                                            console.log(reject)
                                        }
                                    )
                                if (this.state.worker === null)
                                    fetch("http://localhost:3001/customRequest/Select%20*%20from%20workers" +
                                        " where id="+getParamsFromString().workerID).then(
                                        (response)=>{
                                            response.json().then(
                                                (json)=>{
                                                    let newState = {};
                                                    newState.worker = json;
                                                    newState.isLoadedeWorker = 1;
                                                    this.setState(newState);
                                                },
                                                reason => {

                                                }
                                            )
                                        },
                                        (reject)=>{
                                            console.log(reject)
                                        }
                                    )
                                if (this.state.service === null)
                                    fetch("http://localhost:3001/customRequest/Select%20*%20from%20services" +
                                        " where sid="+getParamsFromString().serviceID).then(
                                        (response)=>{
                                            response.json().then(
                                                (json)=>{
                                                    let newState = {};
                                                    newState.service = json;
                                                    newState.isLoadedService = 1;
                                                    this.setState(newState);
                                                },
                                                reason => {

                                                }
                                            )
                                        },
                                        (reject)=>{
                                            console.log(reject)
                                        }
                                    )
                                return "Loading..."
                            }

                        })()}
                    </div>
                </div>
            </div>
        );
    }
}
export default Login
