import React from 'react'
import Place from "./Place";
class Places extends  React.Component{
    constructor() {
        super();
        this.state = {
            isLoaded: false,
            data: []
        }
    }
    getParamsFromString = ()=>{
        let search = window.location.search;
        search = search.split("&");
        let ret = {}
        for (let i of search) {
            ret[i.split("=")[0]]=ret[i.split("=")[1]];
        }
        return ret;
    }
    render(){
        return (
            <div>
                {(()=>{
                    if (!this.state.isLoaded)
                    {
                        let condition = this.props.location.search;
                        condition = condition.replace("?", "");
                        let stringToFetch = "http://localhost:3001/customRequest/Select%20*%20from%20places"

                        fetch(stringToFetch).then(
                            (response)=>{
                                response.json().then(
                                    (json)=>{
                                        let newState = {};
                                        newState.data = json;
                                        newState.isLoaded = true;
                                        this.setState(newState);
                                        console.log(newState)
                                    },
                                    reason => {

                                    }
                                )
                            },
                            (reject)=>{
                                console.log(reject)
                            }
                        )
                        return "Loading..."
                    }
                    else {
                        console.log(this.getParamsFromString())
                        return <div>
                            {this.state.data.map((value)=>{
                                let val = value;

                                return <Place props = {val}/>
                            })}
                        </div>
                    }

                })()}
            </div>
        )
    }
}
export default Places
