                                    " where place.id="+getParamsFromString().placeID;

                                fetch(stringToFetch).then(
                                    (response)=>{
                                        response.json().then(
                                            (json)=>{
                                                let newState = {};
                                                newState.data = json;
                                                newState.isLoaded = true;
                                                this.setState(newState);
                                                console.log(newState)
                                            },
                                            reason => {

                                            }
                                        )
                                    },
                                    (reject)=>{
                                        console.log(reject)
                                    }
                                )
                                return "Loading..."
                            }
                            else {
                                return <div>Loaded</div>
                            }

                        })()}
                    </div>
                </div>
            </div>
        );
    }
}
export default Login
